/**
 * Export all by default
 */
declare const _default: {
    all: typeof all;
    get: typeof get;
    set: typeof set;
};
export default _default;
/**
 * Cookie type
 */
declare type Cookies = {
    [cookie: string]: string;
};
/**
 * Set options
 */
declare type Options = {
    maxage?: number;
    expires?: Date;
    domain?: string;
    path?: string;
    secure?: boolean;
};
/**
 * Set cookie `name` to `value`.
 */
declare function set(name: string, value: string | null, options?: Options): void;
/**
 * Return all cookies.
 *
 * This is isomorphic and may be called
 * from the server-side though it will
 * return nothing.
 */
declare function all(): Cookies;
/**
 * Get cookie `name`.
 */
declare function get(name: string): string | undefined;
