/**
 * Export load
 */
export default load;
/**
 * Types
 */
declare type Options = {
    type?: string;
    charset?: string;
    async?: boolean;
    attrs?: Attributes;
    text?: string;
};
declare type Attributes = {
    [key: string]: string;
};
declare type Callback = (err: Error | null, script: HTMLScriptElement) => void;
/**
 * Load the script
 *
 * Note: The multiple signatures is typescript's overloading
 */
declare function load(src: string, opts: Options, cb: Callback): void;
declare function load(src: string, opts: Callback, cb: void): void;
